import { Component, Input } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-dynamic-form',
  templateUrl: './dynamic-form.component.html',
  styleUrls: ['./dynamic-form.component.scss'],
})
export class DynamicFormComponent {
  @Input() formGroups: any = [];
  form!: FormGroup;

  constructor() {}

  ngOnInit() {
    this.form = this.toFormGroup();
  }

  toFormGroup() {
    const group: any = {};

    this.formGroups.forEach(
      (formGroup: { key: string | number; required: any; value: any }) => {
        group[formGroup.key] = formGroup.required
          ? new FormControl(formGroup.value || '', Validators.required)
          : new FormControl(formGroup.value || '');
      }
    );
    return new FormGroup(group);
  }
}
<div [formGroup]="form">
  <div *ngFor="let formGroupD of formGroups">
    <label [attr.for]="formGroupD.key">{{formGroupD.label}}</label>

  <div [ngSwitch]="formGroupD.controlType">

    <input *ngSwitchCase="'textbox'" [formControlName]="formGroupD.key"
            [id]="formGroupD.key" [type]="formGroupD.type">

    <select [id]="formGroupD.key" *ngSwitchCase="'dropdown'" [formControlName]="formGroupD.key">
      <option *ngFor="let opt of formGroupD.options" [value]="opt.key">{{opt.value}}</option>
    </select>

  </div>

  </div>
</div>
